    getALLMessage(conversion_id) {
        debugger
        axios.get("http://localhost:4000/message/"+conversion_id)
            .then((response)=>{
                debugger
                //this.setState({message: response.data})
                if(response.status===201)
                {
                    alert(response.data);
                }

                let msg=response.data && response.data.message ? response.data.message : [] ;
                let newArr=[];

                msg.map((value,key)=>{
                    let obj={
                        _id: key,
                        text: value.text,
                        createdAt: new Date(value.createdAt),
                        user: {
                            _id: value.sender,
                            name: value.sender_name,
                            avatar: 'data:image/jpeg;base64,'
                        }
                    }
                    newArr.push(obj);
                })

                newArr.reverse();
                this.setState({messages: newArr},()=>{
                    console.log(this.state.messages);
                });

                this.chatChannel = this.pusher.subscribe('new_conversation');
                this.chatChannel.bind('pusher:subscription_succeeded', (res) => {
                    debugger
                    this.chatChannel.bind('new_message', (data) => {
                        debugger
                        let obj={
                            _id: data.message[0].id,
                            text: data.message[0].text,
                            createdAt: new Date(data.message[0].createdAt),
                            user: {
                                _id: data.message[0].sender,
                                name: data.message[0].sender_name,
                                avatar: 'data:image/jpeg;base64,'
                            }
                        }

                        if(this.state.sender!==data.message[0].sender) {
                            this.setState((previousState) => ({
                                messages: GiftedChat.append(previousState.messages, obj),
                            }));
                        }
                    });
                });
            })
            .catch((err)=>{
                alert("Error in Chat: " + err)
            })
    }

